import React, { Fragment } from "react";
import { Field, reduxForm } from "redux-form";
import { Form } from "semantic-ui-react";
import {connect} from "react-redux";

const renderCheckbox = field => (
    <Form.Checkbox
        checked={!!field.input.value}
        name={field.input.name}
        label={field.label}
        onChange={(e, { checked }) => field.input.onChange(checked)}
    />
);

const renderTextArea = ({input, label, placeholder}) => (
    <Form.TextArea
        {...input}
        label={label}
        placeholder={placeholder}
    />
);

const renderTextField = ({input, label, placeholder, meta: {error, touched, warning}}) => {
    return (
        <Fragment><Form.Input
            {...input}
            label={label}
            placeholder={placeholder}
        />
            <div>
                {touched && ((error && <span>{error}</span>) || (warning && <span>{warning}</span>))}
            </div>
        </Fragment>
    )
};

const renderSelect = ({label, input, options, placeholder}) => (
    <Form.Select
        label={label}
        name={input.name}
        onChange={(e, { value }) => input.onChange(value)}
        options={options}
        placeholder={placeholder}
        value={input.value}
    />
);

const renderRadio = field => {
    console.log("field.input.value====>",field.input.value);
    console.log("field.radioValue====>",field.radioValue);
    return (
        <Form.Radio
            checked={field.input.value === field.radioValue}
            label={field.label}
            name={field.input.name}
            onChange={(e, { checked }) => field.input.onChange(field.radioValue)}
        />
    )
};

const required = value => value ? undefined : 'required';

const FormBuilder = props => {
    const { handleSubmit, reset, datas } = props;
    console.log("datas====>",datas);
    return (
        <Fragment>
            <Form onSubmit={handleSubmit}>
                {datas.map((data, i) => {
                    let compType = '';
                    switch (data.type) {
                        case 'checkbox':
                            compType = renderCheckbox;
                            break;
                        case 'text':
                            compType = renderTextField;
                            break;
                        case 'textField':
                            compType = renderTextArea;
                            break;
                        case 'select':
                            compType = renderSelect;
                            break;
                        case 'radio':
                            compType = renderRadio;
                            break;
                        default:
                            compType = '';
                            break;
                    }
                    return <Field
                        Key = {i}
                        component={ compType }
                        {...data}
                        validate={[ required ]}
                        />
                })}
                <Form.Button primary>Submit</Form.Button>
                <Form.Button onClick={reset}>Reset</Form.Button>
            </Form>
        </Fragment>
    )
};

const FormBuilderWrap =  reduxForm({
    form: "profile"
})(FormBuilder);

const mapStateToProps = state => {
    console.log("state==aaa==>",state.form.profile && state.form.profile.values);
    console.log("state.form.profile.submitSucceeded====>",state.form.profile && state.form.profile.submitSucceeded);
    return state.form.profile
        ? {
            values: state.form.profile.values,
            submitSucceeded: state.form.profile.submitSucceeded
        }
        : {};
};

export default connect(mapStateToProps)(FormBuilderWrap);
