import React, { Fragment, forwardRef, useImperativeHandle } from "react";
import { Field, reduxForm } from "redux-form";
import { Form } from "semantic-ui-react";
import {connect} from "react-redux";

const renderCheckbox = ({input, label, meta: {error, touched}}) => {
    return (
        <Fragment>
        <Form.Checkbox
            checked={!!input.value}
            name={input.name}
            label={label}
            onChange={(e, { checked }) => input.onChange(checked)}
        />
        <div>
            {touched && ((error && <span>{error}</span>))}
        </div>
        </Fragment>
    )
};

const renderTextArea = ({input, label, placeholder, meta: {error, touched}}) => {
    return (
        <Fragment>
        <Form.TextArea
            {...input}
            label={label}
            placeholder={placeholder}
        />
        <div>
            {touched && ((error && <span>{error}</span>))}
        </div>
        </Fragment>
    )
};

const renderTextField = ({input, label, placeholder, meta: {error, touched}}) => {
    return (
        <Fragment>
            < Form.Input
            {...input}
            label={label}
            placeholder={placeholder}
            onChange={input.onChange}/>
            <div>
                {touched && ((error && <span>{error}</span>))}
            </div>
        </Fragment>
    )
};

const renderSelect = ({label, input, options, placeholder, meta: {error, touched}}) => {
    return (
        <Fragment>
            <Form.Select
            label={label}
            name={input.name}
            onChange={(e, { value }) => input.onChange(value)}
            options={options}
            placeholder={placeholder}
            value={input.value}/>
        <div>
        {touched && ((error && <span>{error}</span>))}
        </div>
        </Fragment>
    )
};

const renderRadio = ({input, label, radioValue, meta: {error, touched}}) => {
    return (
        <Fragment>
        <Form.Radio
            // TODO: this needs to check
            checked={input.value === radioValue}
            label={label}
            name={input.name}
            onChange={(e, { checked }) => input.onChange(radioValue)}
        />
        <div>
            {touched && ((error && <span>{error}</span>))}
        </div>
        </Fragment>
    )
};

const required = value => {
    return value ? undefined : 'Required'
};
const email = value =>
    value && !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(value) ?
        'Invalid email address' : undefined;
const number = value => value && isNaN(Number(value)) ? 'Must be a number' : undefined;

