import React, { Fragment } from "react";
import { Field, reduxForm } from "redux-form";
import { Form } from "semantic-ui-react";

const renderCheckbox = field => (
    <Form.Checkbox
        checked={!!field.input.value}
        name={field.input.name}
        label={field.label}
        onChange={(e, { checked }) => field.input.onChange(checked)}
    />
);

const renderTextArea = field => (
    <Form.TextArea
        {...field.input}
        label={field.label}
        placeholder={field.placeholder}
    />
);

const renderTextField = ({field, meta: {touched, error, }}) => (
    <Form.Input
        {...field.input}
        label={field.label}
        placeholder={field.placeholder}
    />
);

const renderSelect = field => (
    <Form.Select
        label={field.label}
        name={field.input.name}
        onChange={(e, { value }) => field.input.onChange(value)}
        options={field.options}
        placeholder={field.placeholder}
        value={field.input.value}
    />
);

const renderRadio = field => (
    <Form.Radio
        checked={field.input.value === field.radioValue}
        label={field.label}
        name={field.input.name}
        onChange={(e, { checked }) => field.input.onChange(field.radioValue)}
    />
);

const required = value => value ? undefined : 'required';

const FormBuilder = props => {
    const { handleSubmit, reset, datas } = props;
    console.log("datas====>",datas);
    return (
        <Fragment>
            <Form onSubmit={handleSubmit}>
                {datas.map((data, i) => {
                    let compType = '';
                    switch (data.type) {
                        case 'checkbox':
                            compType = renderCheckbox;
                            break;
                        case 'text':
                            compType = renderTextField;
                            break;
                        case 'textField':
                            compType = renderTextArea;
                            break;
                        case 'select':
                            compType = renderSelect;
                            break;
                        case 'radio':
                            compType = renderRadio;
                            break;
                        default:
                            compType = '';
                            break;
                    }
                    return <Field
                        Key = {i}
                        component={ compType }
                        {...data}
                        validate={[ required ]}
                        />
                })}
                <Form.Button primary>Submit</Form.Button>
                <Form.Button onClick={reset}>Reset</Form.Button>
            </Form>
        </Fragment>
    )
};

export default reduxForm({
    form: "profile"
})(FormBuilder);
