import React from "react";
import { connect } from "react-redux";
import { Container, Divider, Header, Message } from "semantic-ui-react";

import ProfileForm from "./ProfileForm";
import FormBuilder from './formBuilder';
const requireFields = [
    {
        type: 'checkbox',
        label: 'T&C',
        name: 'T&C'
    },
    {
        type: 'text',
        label: 'FirstName',
        name: 'firstName',
        placeholder: 'please enter first name'
    },
    {
        type: 'textField',
        label: 'address',
        name: 'address',
        placeholder: 'please enter placeholder'
    },
    {
        type: 'radio',
        label: 'One',
        name: 'quantity',
        radioValue: 1
    },
    {
        type: 'radio',
        label: 'Two',
        name: 'quantity',
        radioValue: 2
    },
    {
        type: 'select',
        label: 'Gender',
        name: 'gender',
        placeholder: 'please select gender',
        options: [{ key: "m", text: "Male", value: "male" },
                { key: "f", text: "Female", value: "female" }]
    }
];

const App = props => (
  <Container>
    <Divider hidden />

    <Header as="h1" dividing>
      A sample form with Semantic UI React and Redux Form
    </Header>
    {/*<ProfileForm onSubmit={() => console.log("ProfileForm was submitted")} />*/}

    <FormBuilder
        onSubmit={() => console.log("ProfileForm was submitted")}
        datas={requireFields}
    />
    <Message>
      <Message.Header>Form data:</Message.Header>
      <pre>{JSON.stringify(props, null, 2)}</pre>
    </Message>
  </Container>
);

const mapStateToProps = state => {
  return state.form.profile
    ? {
        values: state.form.profile.values,
        submitSucceeded: state.form.profile.submitSucceeded
      }
    : {};
};

export default connect(mapStateToProps)(App);
